(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-08-07 15:07:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-08-11 15:04:03', NULL, 0, 'plain', '', 0, 1, 'info'),
(53, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-08-07 15:07:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-08-07 15:07:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-08-07 15:07:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-08-07 15:07:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2024-08-07 15:07:29', NULL, 0, 'plain', '', 0, 1, 'info'),
(58, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Chọn giao diện?', 'Kiểm tra các giao diện tương thích với WooCommerce và chọn một giao diện phù hợp với thương hiệu và nhu cầu kinh doanh của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-09 14:31:03', NULL, 0, 'plain', '', 0, 1, 'info'),
(59, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Kết nối với WooCommerce.com', 'Kết nối để nhận thông báo và cập nhật sản phẩm quan trọng.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-09 14:37:18', NULL, 0, 'plain', '', 0, 1, 'info'),
(60, 'wc-admin-add-first-product-note', 'email', 'en_US', 'Thêm sản phẩm đầu tiên của bạn', '{greetings}<br /><br />Tuyệt vời; bạn đã tạo một cửa hàng WooCommerce! Bây giờ là lúc để thêm sản phẩm đầu tiên của bạn và sẵn sàng để bắt đầu bán hàng.<br /><br />Có ba cách để thêm sản phẩm của bạn: bạn có thể <strong>tạo sản phẩm thủ công, nhập chúng cùng một lúc qua tệp CSV</strong> hoặc <strong>di chuyển chúng từ dịch vụ khác</strong>.<br /><br /><a href=\"https://woocommerce.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Trực tiếp tài liệu của chúng tôi</a> để biết thêm thông tin hoặc chỉ để bắt đầu!', '{\"role\":\"administrator\"}', 'unactioned', 'woocommerce-admin', '2024-08-09 15:04:13', NULL, 0, 'plain', 'http://localhost/meomaystore/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png', 0, 0, 'info'),
(61, 'wc-admin-mobile-app', 'info', 'en_US', 'Cài đặt ứng dụng di động Woo', 'Cài đặt ứng dụng di động WooCommerce để quản lý đơn đặt hàng, nhận thông báo bán hàng và xem các số liệu chính - mọi lúc mọi nơi.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-09 15:04:13', NULL, 0, 'plain', '', 0, 1, 'info'),
(62, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cách tùy chỉnh danh mục sản phẩm của bạn', 'Bạn muốn danh mục sản phẩm và hình ảnh của mình trông đẹp mắt và phù hợp với thương hiệu của bạn. Hướng dẫn này sẽ cung cấp cho bạn tất cả các mẹo bạn cần để sản phẩm của bạn trông đẹp mắt trong cửa hàng của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-12 15:03:49', NULL, 0, 'plain', '', 0, 1, 'info'),
(63, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bạn đã sẵn sàng để ra mắt cửa hàng của mình?', 'Để đảm bảo bạn không bao giờ có cảm giác chìm đắm \"mình đã quên điều gì\", chúng tôi đã tập hợp danh sách kiểm tra trước khi ra mắt cần thiết.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-12 15:03:49', NULL, 0, 'plain', '', 0, 1, 'info'),
(65, 'wnww-recap-july', 'marketing', 'en_US', 'Powerful updates and upgrades in WooCommerce', 'Have you seen all the latest from Woo? Check out our recent roundup for a look at the newest features, extensions, and insights making WooCommerce perform better than ever.', '{}', 'pending', 'woocommerce.com', '2024-08-13 15:03:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'wc-admin-new-sales-record', 'info', 'en_US', 'Kỷ lục doanh số mới!', 'Woohoo, 13/08/2024 là ngày kỷ lục về doanh số của bạn! Doanh thu thuần là 44.000.000 ₫ vượt qua kỷ lục trước đó là 19.000.000 ₫ được thiết lập vào ngày 12/08/2024.', '{\"old_record_date\":\"2024-08-12\",\"old_record_amt\":19000000,\"new_record_date\":\"2024-08-13\",\"new_record_amt\":44000000}', 'unactioned', 'woocommerce-admin', '2024-08-14 15:04:22', NULL, 0, 'plain', '', 0, 0, 'info');
